import React, { Component } from 'react'
import Container from 'react-bootstrap/Container'
import Button from 'react-bootstrap/Button'
import {
    withRouter
  } from "react-router-dom"

class UnitsComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
        }

        this.handleChange = this.handleChange.bind(this)
        this.refreshUnits = this.refreshUnits.bind(this)
    }

    componentDidMount() {
        this.refreshUnits();
    }

    refreshUnits() {
        console.log("units refreshing!")

        var arry = []

        var unit1 = {
            name: "Flat 1",
            description: "fake desciription flat1",
            build: 2,
            loadIn: 2,
            materials: 7
        }
        var unit2 = {
            name: "Flat 2",
            description: "fake desciription flat2",
            build: 22,
            loadIn: 22,
            materials: 72
        }

        arry.push(unit1)
        arry.push(unit2)

        this.setState({ units: arry })

        var total = {
            build: 24.1,
            loadIn: 24.1,
            materials: 79.1
        }

        this.setState({ totals: total })
    }

    handleChange(event) {
        this.setState(
            {[event.target.name]: event.target.value}
        )
    }

    addUnitClicked() {
        // this.props.history.push(`/projects`)
        this.props.history.push({
            pathname: 'units/new',
            state: { unitId: 1},
        })
    }

    render() {
        return (
            <Container className="mt-3">
                <h2>{this.props.match.params.id}</h2>
                <h2>UNITS</h2>
                <Button onClick={this.addUnitClicked.bind(this)}>add new unit??</Button>
            </Container>
        )
    }
}

export default withRouter(UnitsComponent)
