import React, { Component } from 'react'
import AuthenticationService from '../service/AuthenticationService.jsx';
import ShowService from '../service/ShowService.jsx';

class ShowCardComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            show: props.show
        }
        // this.refreshShows = this.refreshShows.bind(this)
        // this.getName = this.getName.bind(this)
        // this.createShowClicked = this.createShowClicked.bind(this)
    }

    render() {
        console.log('render')
        return (
            <div className="container mt-2 border-custom-card bg-light" onClick={this.props.cardClicked}>

                {/* Show Name and Venue */}
                <div className="row">
                    <div className="col-md-1"></div>
                    <div className="col-md-7">
                        <h4>{this.state.show.name}</h4>
                    </div>
                    <div className="col-md-3 text-right">
                        <h5>{this.state.show.venue}</h5>
                    </div>
                    <div className="col-md-1"></div>
                </div>

                {/* Type and Created Date */}
                <div className="row">
                <div className="col-md-1"></div>
                    <div className="col-md-7">
                        <p>{this.state.show.estimateType} Estimate</p>
                    </div>
                    <div className="col-md-3 text-right">
                        <p>Created 1/15/21</p>
                    </div>
                    <div className="col-md-1"></div>
                </div>
            </div>
        )
    }
}

export default ShowCardComponent
