import React, { Component } from 'react'
import AuthenticationService from '../service/AuthenticationService.jsx';
import ShowService from '../service/ShowService.jsx';
import OverviewComponent from './OverviewComponent.jsx';
// import {Navbar, Nav, NavItem, Button} from 'react-bootstrap';
// import Glyphicon from 'react-bootstrap/lib/Glyphicon'

class ShowComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            showId: this.props.location.state.showId,
            owner: false,
            renderView: 0,
            show: '',

        }
        this.changePage = this.changePage.bind(this)
        this.renderSwitch = this.renderSwitch.bind(this)
        this.refreshShow = this.refreshShow.bind(this)
    }

    componentDidMount() {
        this.refreshShow();
    }

    refreshShow() {
        console.log("here!")
        ShowService.retrieveShow(this.state.showId)
            .then(
                response => {
                    this.setState({ show: response.data })
                }
            )
    }

    changePage(view) {
        this.setState({ renderView: view });
    }

    renderSwitch(which) {
        switch(which) {
            case 0:
                return <OverviewComponent></OverviewComponent>;
            case 1:
                return <div>labor page</div>;
            case 2:
                return <div>summary page</div>;
            case 3:
                return <div>units page</div>;
            default:
                return <div>defualt overview</div>;
        }
    }

    render() {
        console.log('render')
        return (
            <div className="container mt-3">

                <div className="row mt-3 border-bottom-custom">
                    {/* <div className="col"> */}
                    <h1>{this.state.show.name}</h1>
                    {/* </div> */}
                </div>

                {/* <div className="col-md-2">  */}
                <div className="row mt-3 pb-3 border-bottom-custom">
                        <div className="col">
                            <button type="submit" className="btn btn-primary btn-block" name="0" 
                            onClick={(e) => this.toggle(e)}>Labor</button>                       
                        </div>
                        <div className="col">
                            <button type="submit" className="btn btn-primary btn-block" 
                            onClick={this.changePage(1)}>Labor</button>
                        </div>
                        <div className="col">
                            <button type="submit" className="btn btn-primary btn-block" 
                            onClick={this.changePage(2)}>Labor</button>
                        </div>
                        <div className="col">
                            <button type="submit" className="btn btn-primary btn-block" 
                            onClick={this.changePage(3)}>Labor</button>
                        </div>
                    </div>
                {/* </div> */}


                <div className="container row mt-3">
                    {this.renderSwitch(this.state.renderView)}
                </div>

            </div>
        )
    }
}

export default ShowComponent
