import React, { Component } from 'react'
import Container from 'react-bootstrap/Container'
import ProjectService from '../service/ProjectService.jsx'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

class SummaryComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            project: ''
        }

        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event) {
        this.setState(
            {[event.target.name]: event.target.value}
        )
    }

    componentDidMount() {
        this.refreshProjects();
    }

    refreshProjects() {
        ProjectService.retrieveProject(this.props.match.params.id)
            .then(
                response => {
                    this.setState({ project: response.data })
                }
            )
    }

    render() {
        return (
            <Container className="mt-3">

                <Row>
                    <Col>
                        <h2>Project: {this.state.project.name}</h2>
                    </Col>
                    <Col>
                        <h2>Directed by: {this.state.project.director}</h2>
                    </Col>
                </Row>

                <h3>Venue: {this.state.project.venue}</h3>

                <Row>
                    <Col>
                        <h4>Written by: {this.state.project.writer}</h4>
                    </Col>
                    <Col>
                        <h4>Directed by: {this.state.project.director}</h4>
                    </Col>
                </Row>

                <Row>
                    <Col>
                        <h4>Set Designer: {this.state.project.designer}</h4>
                    </Col>
                    <Col>
                        <h4>Technical Director: {this.state.project.technicalDirector}</h4>
                    </Col>
                </Row>
            </Container>
        )
    }
}

export default SummaryComponent
