import React, { Component } from 'react'

class UnitComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            showId: props.showId,
            units: [],
            totalz: ''
        }
        this.refreshUnits = this.refreshUnits.bind(this)
    }

    componentDidMount() {
        this.refreshUnits();
    }

    refreshUnits() {
        console.log("here!")
        // ShowService.retrieveShow(this.state.showId)
        //     .then(
        //         response => {
        //             this.setState({ show: response.data })
        //         }
