import React, { Component } from 'react'
import AuthenticationService from '../service/AuthenticationService';

class NewUserComponent extends Component {

constructor(props) {
        super(props)

        this.state = {
            firstName: '',
            lastName: '',
            username: '',
            password: '',
            passwordConfirm: '',
            hasLoginFailed: false,
            showErrorMessage: false
        }

        this.handleChange = this.handleChange.bind(this)
        this.createClicked = this.createClicked.bind(this)
    }

    handleChange(event) {
        this.setState(
            {
                [event.target.name]
                    : event.target.value
            }
        )
    }

    createClicked() {

        var account
        account.username = this.state.username
        account.password = this.state.password
        account.firstName = this.state.firstName
        account.lastName = this.state.lastName

        AuthenticationService
            .creatAccount(account)
            .then((response) => {
                if (response.success) {
                    AuthenticationService.registerSuccessfulLoginForJwt(this.state.username, response.data.token)
                    this.props.history.push(`/shows`)
                } else {
                    this.setState({ showSuccessMessage: false })
                    this.setState({ hasLoginFailed: true })
                }

            }).catch(() => {

            })
    }

    login() {
        AuthenticationService
        .executeJwtAuthenticationService(this.state.username, this.state.password)
        .then((response) => {
            AuthenticationService.registerSuccessfulLoginForJwt(this.state.username, response.data.token)
            this.props.history.push(`/shows`)
        }).catch(() => {
            this.setState({ showSuccessMessage: false })
            this.setState({ hasLoginFailed: true })
        })
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-md-2"></div>

                    <div className="mx-auto col mt-5 border-custom">
                        <h2>Create Account</h2>
                        <div className="row">
                            <div className="col-md">
                                <div className="form-group">
                                    <label>First Name</label>
                                    <input type="text" className="form-control" placeholder="First Name" id="firstName" />
                                </div>
                            </div>

                            <div className="col-md">
                                <div className="form-group">
                                    <label>Last Name</label>
                                    <input type="text" className="form-control" placeholder="Last Name" id="lastName" />
                                </div>
                            </div>
                        </div>
                        <div className="form-group">
                            <label>Email address</label>
                            <input type="email" className="form-control" placeholder="Enter email" id="username" />
                        </div>

                        <div className="row">
                            <div className="col-md">
                                <div className="form-group">
                                    <label>Password</label>
                                    <input type="password" className="form-control" placeholder="Enter password" id="password" />
                                </div>
                            </div>

                            <div className="col-md">
                                <div className="form-group">
                                    <label>Confirm Password</label>
                                    <input type="password" className="form-control" placeholder="Confirm Password" id="confirmPassword" />
                                </div>
                            </div>
                        </div>
                        <small id="passwordHelpBlock" class="form-text text-muted">
                            Your password must be 8-20 characters long, contain letters and numbers, and must not contain spaces, special characters, or emoji.
                        </small>

                        <button type="submit" className="btn btn-primary btn-block" onClick={this.createClicked}>Submit</button>
                    </div>
                    <div className="col-md-2"></div>
                </div>
            </div>
        )
    }
}

export default NewUserComponent
