import React, { Component } from 'react'
import AuthenticationService from '../service/AuthenticationService';

class NewUserComponent extends Component {

constructor(props) {
        super(props)

        this.state = {
            firstName: '',
            lastName: '',
            username: '',
            password: '',
            passwordConfirm: '',
            hasLoginFailed: false,
            showSuccessMessage: false
        }

        this.handleChange = this.handleChange.bind(this)
        this.createClicked = this.createClicked.bind(this)
    }

    handleChange(event) {
        this.setState(
            {
                [event.target.name]
                    : event.target.value
            }
        )
    }

    createClicked() {
        // if(this.state.username==='user' && this.state.password==='password'){
        //     AuthenticationService.registerSuccessfulLogin(this.state.username,this.state.password)
        //     this.setState({showSuccessMessage:true})
        //     this.setState({hasLoginFailed:false})
        // }
        // else {
        //      this.setState({showSuccessMessage:false})
        //      this.setState({hasLoginFailed:true})
        // }
        // if

        var account
        account.username = this.state.username
        account.password = this.state.password
        account.firstName = this.state.firstName
        account.lastName = this.state.lastName

        AuthenticationService
            .creatAccount(account)
            .then((response) => {
                AuthenticationService.registerSuccessfulLoginForJwt(this.state.username, response.data.token)
                this.props.history.push(`/shows`)
            }).catch(() => {
                this.setState({ showSuccessMessage: false })
                this.setState({ hasLoginFailed: true })
            })
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-md-2"></div>

                    <div className="mx-auto col mt-5 border-custom">
                        <h2>Create Account</h2>
                        <div className="row">
                            <div className="col-md">
                                <div className="form-group">
                                    <label>First Name</label>
                                    <input type="text" className="form-control" placeholder="First Name" value={this.state.firstName} />
                                </div>
                            </div>

                            <div className="col-md">
                                <div className="form-group">
                                    <label>Last Name</label>
                                    <input type="text" className="form-control" placeholder="Last Name" value={this.state.lastName} />
                                </div>
                            </div>
                        </div>
                        <div className="form-group">
                            <label>Email address</label>
                            <input type="email" className="form-control" placeholder="Enter email" value={this.state.username} />
                        </div>

                        <div className="row">
                            <div className="col-md">
                                <div className="form-group">
                                    <label>Password</label>
                                    <input type="password" className="form-control" placeholder="Enter password" value={this.state.password} />
                                </div>
                            </div>

                            <div className="col-md">
                                <div className="form-group">
                                    <label>Confirm Password</label>
                                    <input type="password" className="form-control" placeholder="Confirm Password" value={this.state.passwordConfirm} />
                                </div>
                            </div>
                        </div>

                        <button type="submit" className="btn btn-primary btn-block" onClick={this.createClicked}>Submit</button>
                    </div>
                    <div className="col-md-2"></div>
                </div>
            </div>
        )
    }
}

export default NewUserComponent
