        this.setState({
            materials
        });
    };

    handleMaterialAmountChange(index, item, event) {

        const materials = [...this.state.materials];

        item.amountRequired = event.target.value 
        materials.splice(index, 1, item); // replaces 1 element at specificed index

        this.setState({
            materials
        });
    };

