import React, { Component } from 'react'
import Container from 'react-bootstrap/Container'
import Button from 'react-bootstrap/Button'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Form from 'react-bootstrap/Form';

class AddUnitComponent extends Component {

    constructor(props) {
        super(props)

        this.state = {
            name: '',
            description: '',
            UnitSubcomponents: [],
            subComponentForms: [],
            list: [1, 2, 3],
            value: 8
        }

        this.handleChange = this.handleChange.bind(this)
    }

    addSubComClicked() {
        console.log("CLICKED???")
        // this.state.subComponentForms.push(<p>here</p>)
        // this.setState({ subComponentForms: subComponentForms.push(<p>g</p>) })
        // // this.setState(state => {
        // //     const list = state.subComponentForms.concat(<p>ADDING</p>);

        // //     return {
        // //         state.subComponentForms,
        // //     };
        // //   });
        // this.setState(state => {
        //     const list = [this.state.subComponentForms, 9];

        //     return {
        //         list,
        //     };
        //   });

        this.setState(state => {
            const list = [...state.subComponentForms, 8];

            return {
                subComponentForms,
            };
          });
    }

    cancelClicked() {
        this.props.history.goBack()
    }

    addClicked() {
        console.log("adding new unit from add unit compoenent ")
        this.props.history.goBack()
    }

    handleChange(event) {
        this.setState(
            {[event.target.name]: event.target.value}
        )
    }

    render() {
        return (
            <Container className="mt-3">

                <h2 className="border-bottom-custom">Add Unit</h2>

                <Form>

                    {/* Name First and Last */}
                    <Row>
                        <Col>
                        <Form.Group controlId="formGroupFirstName">
                            <Form.Label>First Name</Form.Label>
                            <Form.Control type="text" placeholder="First Name" 
                            name="firstName" value={this.state.firstName} onChange={this.handleChange}/>
                        </Form.Group>
                        </Col>

                        <Col>
                        <Form.Group controlId="formGroupLastName">
                            <Form.Label>Last Name</Form.Label>
                            <Form.Control type="text" placeholder="Last Name" 
                            name="lastName" value={this.state.lastName} onChange={this.handleChange}/>
                        </Form.Group>
                        </Col>
                    </Row>

                    {/* <div id="dynamicInput">
                       {this.state.inputs.map(input => <Form.Group key={input} />)}
                   </div> */}

                   <div>
                       {this.state.subComponentForms.map(
                           input => 
                           <p>{input}</p>)}
                   </div>


                </Form>

                <Row>
                    <Col xs={2}/>
                    <Col>
                        <Button block onClick={this.addSubComClicked.bind(this)}>Add Subcomponent</Button>
                    </Col>
                    <Col xs={2}/>
                </Row>

                <Row className="mt-3">
                    <Col>
                        <Button variant="secondary" block onClick={this.cancelClicked.bind(this)}>Cancel</Button>
                    </Col>
                    <Col xs={4}/>
                    <Col>
                        <Button block onClick={this.addClicked.bind(this)}>Add Unit</Button>
                    </Col>
                </Row>

            </Container>
        )
    }
}

export default AddUnitComponent
