import React, { Component } from 'react'
import AuthenticationService from '../service/AuthenticationService.jsx';
import ShowService from '../service/ShowService.jsx';

class ShowCardComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            name: 'Current User',
            shows: [],
            message: null
        }
        this.refreshShows = this.refreshShows.bind(this)
        this.getName = this.getName.bind(this)
        this.createShowClicked = this.createShowClicked.bind(this)
    }

    componentDidMount() {
        this.refreshShows();
        this.getName();
    }

    refreshShows() {
        ShowService.retrieveAllShows()
            .then(
                response => {
                    this.setState({ shows: response.data })
                }
            )
    }

    getName() {
        AuthenticationService.getLoggedInUsersNameFirstLast()
            .then(
                response => {
                    this.setState({ name: response.data })
                }
            )
    }

    createShowClicked() {
        this.props.history.push(`/shows/create`)
    }

    render() {
        console.log('render')
        return (
            <div className="container mt-2 border-custom-card">

                {/* Show Name and Venue */}
                <div className="row">
                    <div className="col-md-1"></div>
                    <div className="col-md-7">
                        <h4>Show Name</h4>
                    </div>
                    <div className="col-md-3 d-flex justify-content-end">
                        <h5>JMU</h5>
                    </div>
                    <div className="col-md-1"></div>
                </div>

                {/* Type and Created Date */}
                <div className="row">
                <div className="col-md-1"></div>
                    <div className="col-md-7">
                        <p>Scenery Estimate</p>
                    </div>
                    <div className="col-md-3 d-flex justify-content-end">
                        <p>Created 1/15/21</p>
                    </div>
                    <div className="col-md-1"></div>
                </div>
            </div>
        )
    }
}

export default ShowCardComponent
