import React, { Component } from 'react'
import AuthenticationService from '../service/AuthenticationService.jsx';
import ShowService from '../service/ShowService.jsx';
// import {Navbar, Nav, NavItem, Button} from 'react-bootstrap';
// import Glyphicon from 'react-bootstrap/lib/Glyphicon'
import SidebarComponent from './SidebarComponent.jsx';

class ShowComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            showId: this.props.location.state.showId,
            owner: false,
            renderView: 0,
            show: ''
        }
    }

    handleChange(event) {
        this.setState(
            {[event.target.name]: event.target.value}
        )
    }

    componentDidMount() {
        this.refreshShow();
    }

    refreshShow() {
        ShowService.retrieveShow(this.state.showId)
            .then(
                response => {
                    this.setState({ show: response.data })
                }
            )
    }

    renderSwitch(which) {
        switch(which) {
            case 0:
                return <div>overview page</div>;
            case 1:
                return <div>labor page</div>;
            case 2:
                return <div>summary page</div>;
            case 3:
                return <div>units page</div>;
            default:
                return <div>defualt overview</div>;
        }
      }


    render() {
        console.log('render')
        return (
            <div className="container mt-3 row">

                <div className="col-md-2"> 
                    <div className="col">
                        <div className="row"
                        onClick={() => this.setState({ renderView: 0 })}>
                            Overview
                        </div>
                        <div className="row"
                        onClick={() => this.setState({ renderView: 1 })}>
                            Labor
                        </div>
                        <div className="row"
                        onClick={() => this.setState({ renderView: 2 })}>
                            Summary
                        </div>
                        <div className="row"
                        onClick={() => this.setState({ renderView: 3 })}>
                            Units
                        </div>
                    </div>
                </div>

                <div className="col-md-10">
                    <div className="row">
                        Show! {this.state.showId} {this.state.show.name}
                    </div>
                    {this.renderSwitch(this.state.renderView)}
                </div>

            </div>
        )
    }
}

export default ShowComponent
